/**
 * Enhanced function to extract and clean appointment data from JSON string
 * @param {string} input - Raw JSON string to parse
 * @return {object} - Clean object with only necessary appointment attributes
 */
function extractAppointmentData(input) {
  try {
    // Remove any markdown formatting and clean up the input
    let cleanedInput = input.replace(/```json\\n|\\n```|```json|```/g, '');
    cleanedInput = cleanedInput.replace(/\\n/g, '\n');

    // Parse the JSON string
    const parsedData = JSON.parse(cleanedInput);

    // Extract only the necessary fields for appointment management
    const appointmentData = {
      patientName: parsedData.patientName,
      patientEmail: parsedData.patientEmail,
      appointmentDate: parsedData.appointmentDate,
      appointmentTime: parsedData.appointmentTime,
      appointmentDay: parsedData.appointmentDay,
      cancellationReason: parsedData.cancellationReason,
      formattedDateTime: parsedData.formattedDateTime
    };

    return appointmentData;
  } catch (error) {
    console.error('Error parsing JSON:', error.message);
    return { 
      error: 'Failed to parse appointment data',
      details: error.message
    };
  }
}

