// Function to clean and parse the JSON from the cancellation handler
function parseAndCleanJSON(input) {
  try {
    // Remove markdown code block formatting (```json and ```)
    let cleanedInput = input.replace(/```json\n|\n```/g, '');

    // Replace literal "\n" strings with actual newlines for proper parsing
    cleanedInput = cleanedInput.replace(/\\n/g, '\n');

    // Remove any remaining markdown code block indicators
    cleanedInput = cleanedInput.replace(/```[a-z]*|```/g, '');

    // Trim any whitespace
    cleanedInput = cleanedInput.trim();

    // Try to parse the cleaned JSON
    const parsedData = JSON.parse(cleanedInput);
    return parsedData;
  } catch (error) {
    // If there's an error, return the error message for debugging
    return { 
      error: error.message, 
      originalInput: input,
      cleanedAttempt: cleanedInput
    };
  }
}

