// STEP 1: Extract the patient name and appointment datetime we're looking for
// This will come from the Field Extractor or other source
let targetPatientName = null;
let targetDateTime = null;

// More thorough search for patient data
function extractTargetPatient(data) {
  console.log("Searching for target patient information...");

  // Direct property search
  if (data.patientName && typeof data.patientName === 'string') {
    targetPatientName = data.patientName;
    console.log(`Found target patient name directly: "${targetPatientName}"`);
  }

  if (data.formattedDateTime && typeof data.formattedDateTime === 'string') {
    targetDateTime = data.formattedDateTime;
    console.log(`Found target datetime directly: "${targetDateTime}"`);
  }

  // Search recursively
  function searchForPatient(obj, path = '') {
    if (!obj || typeof obj !== 'object') return;

    // Check top-level properties
    if (obj.patientName && typeof obj.patientName === 'string' && !targetPatientName) {
      targetPatientName = obj.patientName;
      console.log(`Found target patient name at ${path}.patientName: "${targetPatientName}"`);
    }

    if (obj.formattedDateTime && typeof obj.formattedDateTime === 'string' && !targetDateTime) {
      targetDateTime = obj.formattedDateTime;
      console.log(`Found target datetime at ${path}.formattedDateTime: "${targetDateTime}"`);
    }

    // Check Field Extractor format
    if (obj.validJSON && typeof obj.validJSON === 'object') {
      if (obj.validJSON.patientName && !targetPatientName) {
        targetPatientName = obj.validJSON.patientName;
        console.log(`Found target patient name at ${path}.validJSON.patientName: "${targetPatientName}"`);
      }
      if (obj.validJSON.formattedDateTime && !targetDateTime) {
        targetDateTime = obj.validJSON.formattedDateTime;
        console.log(`Found target datetime at ${path}.validJSON.formattedDateTime: "${targetDateTime}"`);
      }
    }

    // Search nested objects
    for (const key in obj) {
      if (obj[key] && typeof obj[key] === 'object') {
        searchForPatient(obj[key], path ? `${path}.${key}` : key);
      }
    }
  }

  // Start recursive search
  if (Array.isArray(data)) {
    for (let i = 0; i < data.length; i++) {
      searchForPatient(data[i], `items[${i}]`);
    }
  } else {
    searchForPatient(data);
  }

  // CRITICAL: Hardcode for testing based on your calendar data
  // Remove this in production, this is just to verify our matching logic
  if (!targetPatientName) {
    console.log("WARNING: Using hardcoded test patient name 'John Smith'");
    targetPatientName = "John Smith";
  }

  if (!targetDateTime) {
    console.log("WARNING: Using hardcoded test date '2025-05-12T08:00:00+08:00'");
    targetDateTime = "2025-05-12T08:00:00+08:00";
  }

  return {
    patientName: targetPatientName,
    appointmentDateTime: targetDateTime
  };
}

// STEP 2: Extract all calendar events that we need to check against
function extractCalendarEvents(data) {
  const events = [];
  console.log("Extracting calendar events...");

  // Helper function to extract event details
  function extractEvent(obj, source = 'unknown') {
    if (obj.id && obj.summary && obj.dateTime) {
      events.push({
        id: obj.id,
        summary: obj.summary,
        dateTime: obj.dateTime,
        source
      });
      console.log(`Extracted event directly - ID: ${obj.id}, Summary: ${obj.summary}`);
      return true;
    }

    if (obj.id && obj.summary && obj.start && obj.start.dateTime) {
      events.push({
        id: obj.id,
        summary: obj.summary,
        dateTime: obj.start.dateTime,
        source
      });
      console.log(`Extracted Google Calendar event - ID: ${obj.id}, Summary: ${obj.summary}`);
      return true;
    }

    return false;
  }

  // Process array items
  if (Array.isArray(data)) {
    console.log(`Processing ${data.length} items in array`);
    for (let i = 0; i < data.length; i++) {
      const item = data[i];

      // Try direct extraction
      if (extractEvent(item, `items[${i}]`)) continue;

      // Check if this is a Google Calendar event
      if (item.json) {
        const extracted = extractEvent(item.json, `items[${i}].json`);
        if (extracted) continue;
      }

      // If it's a node with content
      if (item.content) {
        if (Array.isArray(item.content)) {
          console.log(`Found nested array in items[${i}].content with ${item.content.length} items`);
          for (let j = 0; j < item.content.length; j++) {
            extractEvent(item.content[j], `items[${i}].content[${j}]`);
          }
        } else if (typeof item.content === 'object') {
          extractEvent(item.content, `items[${i}].content`);
        }
      }
    }
  } else if (typeof data === 'object') {
    // Try direct extraction from object
    extractEvent(data, 'items');

    // Handle special case where data is directly from Google Calendar API
    if (data.items && Array.isArray(data.items)) {
      console.log(`Found Google Calendar API response with ${data.items.length} events`);
      for (let i = 0; i < data.items.length; i++) {
        extractEvent(data.items[i], `items.items[${i}]`);
      }
    }
  }

  // Direct access for your specific structure - based on error output
  // This is for testing with your specific data structure
  if (events.length === 0) {
    console.log("No events found using standard extraction. Trying direct access for your structure...");

    if (Array.isArray(data) && data.length > 0) {
      // Based on your output showing events at [0].json, [1].json, etc.
      for (let i = 0; i < data.length; i++) {
        if (data[i] && data[i].json) {
          const event = data[i].json;
          if (event.id && event.summary) {
            // Make sure we have a dateTime
            const dateTime = event.dateTime || 
                           (event.start && event.start.dateTime) || 
                           event.date || 
                           event.time;

            if (dateTime) {
              events.push({
                id: event.id,
                summary: event.summary,
                dateTime: dateTime,
                source: `[${i}].json`
              });
              console.log(`Directly extracted event from [${i}].json - ID: ${event.id}, Summary: ${event.summary}`);
            }
          }
        }
      }
    }
  }

  return events;
}

