// STEP 3: Find matches between target patient and calendar events
function findMatches(patient, events) {
  console.log(`\nATTENTION: Looking for matches for patient "${patient.patientName}" and appointment on "${patient.appointmentDateTime}"`);

  const matches = [];

  for (const event of events) {
    console.log(`\nChecking event: "${event.summary}" (${event.id})`);

    // Check name match
    let nameMatch = false;
    if (patient.patientName && event.summary) {
      const patientName = patient.patientName.toLowerCase().trim();
      const summary = event.summary.toLowerCase().trim();

      nameMatch = summary.includes(patientName);
      console.log(`- Name check: ${nameMatch ? "MATCHED" : "No match"} - Looking for "${patientName}" in "${summary}"`);

      // Additional check for appointment patterns
      if (!nameMatch) {
        const patterns = ["appointment with", "meeting with", "consultation with"];
        for (const pattern of patterns) {
          if (summary.includes(pattern)) {
            const remainingText = summary.split(pattern)[1];
            if (remainingText && remainingText.toLowerCase().trim().includes(patientName)) {
              nameMatch = true;
              console.log(`- Pattern match: MATCHED using pattern "${pattern}"`);
              break;
            }
          }
        }

        // Check for "Patient: Name" pattern
        if (summary.includes("patient:")) {
          const patientPart = summary.split("patient:")[1].trim();
          if (patientPart.toLowerCase().includes(patientName)) {
            nameMatch = true;
            console.log(`- Patient label match: MATCHED using "Patient:" label`);
          }
        }
      }
    }

    // Check date match
    let dateMatch = false;
    if (patient.appointmentDateTime && event.dateTime) {
      try {
        const targetDate = new Date(patient.appointmentDateTime);
        const eventDate = new Date(event.dateTime);

        // First check exact match
        if (targetDate.toISOString() === eventDate.toISOString()) {
          dateMatch = true;
          console.log(`- Date check: MATCHED exactly - ${targetDate.toISOString()} = ${eventDate.toISOString()}`);
        } else {
          // Then check just the date part (ignoring time)
          const targetDateOnly = targetDate.toISOString().split('T')[0];
          const eventDateOnly = eventDate.toISOString().split('T')[0];

          if (targetDateOnly === eventDateOnly) {
            dateMatch = true;
            console.log(`- Date check: MATCHED date part - ${targetDateOnly} = ${eventDateOnly}`);
          } else {
            // Log dates for debugging
            console.log(`- Date check: NO MATCH - Target date ${targetDateOnly} â‰  Event date ${eventDateOnly}`);
          }
        }
      } catch (e) {
        console.log(`- Date check: ERROR comparing dates - ${e.message}`);
        console.log(`  Target: ${patient.appointmentDateTime}, Event: ${event.dateTime}`);
      }
    }

    // For this version, we're adding partial matching to see all candidates
    const matchScore = {
      nameMatched: nameMatch,
      dateMatched: dateMatch,
      score: (nameMatch ? 1 : 0) + (dateMatch ? 1 : 0)
    };

    if (nameMatch && dateMatch) {
      console.log(`âœ… FULL MATCH FOUND: "${event.summary}" (${event.id})`);
      matches.push({...event, matchScore});
    } else if (nameMatch || dateMatch) {
      console.log(`ðŸŸ  PARTIAL MATCH: "${event.summary}" - Name: ${nameMatch}, Date: ${dateMatch}`);
      matches.push({...event, matchScore, partial: true});
    } else {
      console.log(`âŒ NO MATCH: "${event.summary}"`);
    }
  }

  // Sort matches by score (full matches first)
  matches.sort((a, b) => b.matchScore.score - a.matchScore.score);

  return matches;
}

// Find matches
const matches = findMatches(targetPatient, calendarEvents);
console.log(`\nFound ${matches.length} matching events (${matches.filter(m => !m.partial).length} full matches, ${matches.filter(m => m.partial).length} partial matches)`);

// STEP 4: Return the results
let result;
if (matches.length === 0) {
  // No matches at all
  result = {
    matchFound: false,
    message: "No matching events found in calendar",
    actionNeeded: "No action needed - no events to delete",
    diagnostics: {
      targetPatient,
      eventsChecked: calendarEvents.length,
      calendarEvents: calendarEvents
    }
  };
} else {
  // Found at least one match
  const fullMatches = matches.filter(m => !m.partial);

  if (fullMatches.length === 1) {
    // Perfect - exactly one full match
    const match = fullMatches[0];
    result = {
      matchFound: true,
      eventId: match.id,
      eventDetails: {
        id: match.id,
        summary: match.summary,
        dateTime: match.dateTime,
        patientName: targetPatient.patientName,
        formattedDateTime: targetPatient.appointmentDateTime
      },
      message: "Found exactly one matching event!",
      actionNeeded: "This event can be deleted from Google Calendar",
      diagnostics: {
        targetPatient,
        eventsChecked: calendarEvents.length,
        matchCriteria: match.matchScore
      }
    };
  } else if (fullMatches.length > 1) {
    // Multiple full matches - this is concerning
    result = {
      matchFound: true,
      multipleMatches: true,
      eventCount: fullMatches.length,
      eventIds: fullMatches.map(e => e.id),
      primaryEventId: fullMatches[0].id, // Use the first match as primary
      eventDetails: fullMatches.map(e => ({
        id: e.id,
        summary: e.summary,
        dateTime: e.dateTime,
        matchScore: e.matchScore
      })),
      message: `Found ${fullMatches.length} fully matching events - recommend manual verification`,
      actionNeeded: "Multiple matches found - please verify before deletion",
      diagnostics: {
        targetPatient,
        eventsChecked: calendarEvents.length,
        matchingEvents: fullMatches
      }
    };
  } else {
    // Only partial matches
    const bestMatch = matches[0]; // First one is highest scoring
    result = {
      matchFound: true,
      partialMatchOnly: true,
      eventId: bestMatch.id,
      eventDetails: {
        id: bestMatch.id,
        summary: bestMatch.summary,
        dateTime: bestMatch.dateTime,
        patientName: targetPatient.patientName,
        formattedDateTime: targetPatient.appointmentDateTime,
        matchScore: bestMatch.matchScore
      },
      message: `Found partial match: ${bestMatch.matchScore.nameMatched ? 'Name matched' : 'Name did not match'}, ${bestMatch.matchScore.dateMatched ? 'Date matched' : 'Date did not match'}`,
      actionNeeded: "Partial match found - please verify before deletion",
      diagnostics: {
        targetPatient,
        eventsChecked: calendarEvents.length,
        matchCriteria: bestMatch.matchScore,
        partialMatches: matches
      }
    };
  }
}

console.log("Final result:", JSON.stringify(result, null, 2));
return result;
